css:
@-moz-document domain('www.pinterest.com'), regexp('(.*)\.pinterest\.(.+)\/.+') {
/* pinterest */

body,
mains,
article {
    overflow: auto !important;
    height: 100% !important;
}

/* Hide Header / Footer */
.outerCookieBar,
div[data-test-id="unauthEuCookieBar"],
div[data-test-id="giftWrap"] {
    display: none !important;
    visibility: hidden !important;
}

/* Hide Login */
div[data-test-id="login"] {
    display: none !important;
    visibility: hidden !important;
    background-color: red !important;
    color: red !important;
}

div[data-test-id="fullPageSignupModal"],
div[data-test-id="fullPageSignupModal"] > div:nth-child(1) {
    overflow: auto !important;
    background-color: transparent !important;
    opacity: 1 !important;
    z-index: 0 !important;
    color: red !important;
}

div[data-test-id="fullPageSignupModal"] {
    display: none !important;
    visibility: hidden !important;
}

}
