/*
    
    Theme Name: Disney+ Recolor Plus

    Author: Adam-J-Frost.

    Updated: 2020/01/07

    Created: 2020/01/04
    
    Description:
    Change the colors of Disney+ to your preference!
    I hope you like this style and thank you for using it!

*/
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.disneyplus.com") {
/*[[color]]*/

/* <- Main Page Start -> */
.jMgGEX,
.jMgGEX,
.oVBuT,
.kFdgyQ,
.leujzN,
.dnmafp button,
.lhsUOd,
fieldset.sc-bGbJRg > span > p,
.sc-nrwXf > p,
.ezqMDu,
.iGvYeL,
.kPdPtK {
    color: var(--a-color) !important;
}

.jMgGEX:hover,
.jMgGEX:focus,
.oVBuT:hover,
.oVBuT:focus,
.kFdgyQ:hover,
.kFdgyQ:focus,
.leujzN:hover,
.leujzN:focus,
.dnmafp button:hover,
.lhsUOd:hover,
.lhsUOd:focus,
fieldset.sc-bGbJRg > span > p:hover,
.sc-nrwXf > p:hover,
.ezqMDu:hover,
.ezqMDu:focus,
.ezqMDu:active,
.iGvYeL:hover,
.iGvYeL:focus,
.iGvYeL:active,
.kPdPtK:hover,
.kPdPtK:focus,
.kPdPtK:active {
    color: var(--a-hover-color) !important;
}

.cDQHvO > path {
    fill: var(--arrow-color) !important;
}

#body-logo,
.sc-dqBHgY > img,
.Aucj .btm-media-player .btm-media-overlays-container .overlay__no-media .no-media-logo {
    background-image: var(--logo) !important;
    background-size: 208px 130px !important;
    background-repeat: no-repeat !important;
    width: 208px !important;
    height: 130px !important;
    box-sizing: border-box !important;
    padding-left: 500px !important;
}

#nav-list > img:nth-child(1),
.sc-fAjcbJ > img:nth-child(1),
#logo[alt="Disney Plus Logo"],
.btm-media-player .btm-media-overlays-container .overlay.overlay__ratings .overlay__ratings_brand img {
    background-image: var(--logo) !important;
    background-size: 88px 50px !important;
    background-repeat: no-repeat !important;
    width: 88px !important;
    height: 50px !important;
    box-sizing: border-box !important;
    padding-left: 120px !important;
}
.sc-Rmtcm {
    background-image: var(--loader-icon) !important;
    background-size: 32px 32px !important;
    background-repeat: no-repeat !important;
    width: 32px !important;
    height: 32px !important;
    box-sizing: border-box !important;
    padding-left: 32px !important;
}
.jZqXkM:hover a p::before,
.jZqXkM:focus a p::before,
.jZqXkM:hover a span::before,
.jZqXkM:focus a span::before {
    background-color: var(--border-color) !important;
}
.kgpZjI,
.cgYqRj,
.cltAWC,
.jCsvWk,
.iBmocc {
    border-radius: 4px !important;
    background: var(--button-color) !important;
    color: var(--button-text-color) !important;
}
.kgpZjI:hover,
.kgpZjI:focus,
.kgpZjI:active,
.cgYqRj:hover,
.cgYqRj:focus,
.cltAWC:hover,
.cltAWC:focus,
.cltAWC:avtive,
.jCsvWk:hover,
.jCsvWk:focus,
.jCsvWk:active,
.iBmocc:hover,
.iBmocc:focus {
    border-radius: 4px !important;
    background: var(--button-hover-color) !important;
    color: var(--button-text-hover-color) !important;
}
.fVbZJa,
.tjAJK {
    background: var(--button-color)!important;
    border-color: var(--button-icon-border-color) !important;
}
.fVbZJa:hover,
.tjAJK:hover {
    background: var(--button-icon-border-hover-color) !important;
    border-color: var(--button-icon-border-hover-color) !important;
}
.fVbZJa span,
.tjAJK span {
    background-color: var(--checkmark-a-color) !important;
}
.fVbZJa:hover span,
.tjAJK:hover span {
    background-color: var(--checkmark-a-color) !important;
}
.sc-LKuAh > svg:nth-child(1) > path:nth-child(1) {
    fill: var(--button-icon-color) !important;
}
.sc-LKuAh > svg:nth-child(1) > path:nth-child(1):hover {
    fill: var(--button-icon-hover-color) !important;
}
.kNDfzO path {
    fill: var(--arrow-color) !important;
}
.koeCJs path {
    fill: var(--arrow-color) !important;
}
.XbJOJ {
    border: 2px solid var(--border-color) !important;
}
.AAeIq .slick-dots li.slick-active button::before,
.cAPbBa .slick-dots li.slick-active button::before {
    color: var(--dot-color) !important;
}
.ggYhHw {
    background: var(--border-color) !important;
}
.hyGSHI:hover::after,
.hyGSHI:focus::after,
.hyGSHI:focus-within::after {
    border: 4px solid var(--border-color) !important;
}
.kaxcVW:hover::after,
.kCwADT:hover::after,
.hcOFUn:hover::after,
.gYkzzC:hover::after,
.brFamM:hover::after,
.igwcUM:hover::after,
.hLUPvG:hover::after,
.cVTxEH:hover::after,
.cVTxEH:focus::after,
.lmLWqo:hover::after,
.lmLWqo:focus::after,
.kAqQeT:hover::after {
    border-color: var(--border-color) !important;
}
.cVTxEH:hover,
.cVTxEH:focus {
    background-color: rgba(0, 0, 0, 0.4);
    transform: scale(1.05);
}
.caQIul:hover::after,
.caQIul:focus::after,
.caQIul:focus-within::after {
    border: 4px solid var(--border-color) !important;
}
.dWMHwh,
.dWMHwh {
    background: var(--button-color) !important;
}
.dWMHwh:hover,
.dWMHwh:focus {
    background: var(--button-hover-color) !important;
}
.euZfrL path {
    fill: var(--button-icon-color) !important;
}
.dWMHwh {
    color: var(--button-text-color) !important;
}
.dWMHwh:hover,
.dWMHwh:focus {
    color: var(--button-text-color) !important;
}
.eyEzDn,
.eyEzDn,
.eyEzDn.eyEzDn {
    border: 1px solid var(--border-color) !important;
    color: var(--button-text-color) !important;
}
.eyEzDn,
.eyEzDn {
    background: var(--button-background-transparent-color) !important;
}
.eyEzDn:hover,
.eyEzDn:focus {
    background: var(--button-background-transparent-hover-color) !important;
}
.eyEzDn:hover,
.eyEzDn:focus,
.eyEzDn.eyEzDn:active {
    border: 1px solid var(--border-color) !important;
    color: var(--button-text-color) !important;
}
progress.sc-kkGfuU {
    background: var(--border-color) !important;
    border: 1px solid var(--border-color) !important;
}
progress.sc-kkGfuU:hover {
    background: var(--border-color) !important;
    border: 1px solid var(--border-color) !important;
}
.\39 > g > g > g > g > path {
    fill: var(--logo-color) !important;
}
.gzTSjZ:checked + span {
    background-color: var(--border-color);
    border: medium none;
}
.bkiCgY path {
    fill: var(--checkmark-b-color) !important;
}

/* <- Update Start -> */
.welcome-page__content__button,
[aria-label="AGREE & CONTINUE"],
[aria-label="Agree and continue"],
[aria-label="LOG IN"],
[aria-label="Continue"],
[aria-label="Log in to your Disney Plus account."],
[aria-label="Done"],
:checked + span[for="autoplay"],
:checked + span[for="backgroundVideo"],
[aria-label="Done. Select this to save your profiles and leave this screen."],
.skipToContentTarget[role="button"],
.btm-media-player .btm-media-overlays-container .overlay__skip .skip__button {
    background-color: var(--button-color) !important;
    border-color: var(--border-color) !important;
    color: var(--button-text-color) !important;
}
.welcome-page__content__button:hover,
[aria-label="AGREE & CONTINUE"]:hover,
[aria-label="Agree and continue"]:hover,
[aria-label="LOG IN"]:hover,
[aria-label="Continue"]:hover,
[aria-label="Log in to your Disney Plus account."]:hover,
[aria-label="Done"]:hover,
:checked + span[for="autoplay"]:hover,
:checked + span[for="backgroundVideo"]:hover,
[aria-label="Done. Select this to save your profiles and leave this screen."]:hover,
.btm-media-player .btm-media-overlays-container .overlay__skip .skip__button:hover {
    background-color: var(--button-hover-color) !important;
    border-color: var(--button-hover-color) !important;
    color: var(--button-text-hover-color) !important;
}
.lnTsNm:hover a p::before,
.lnTsNm:focus a p::before,
.lnTsNm:hover a span::before,
.lnTsNm:focus a span::before {
    background-color: var(--border-color) !important;
}
.fRrqVX svg path {
    fill: var(--button-icon-color) !important;
}
a[href="https://disneyprivacycenter.com/company-overview/"],
[color="#67bdff"],
[aria-label="Sign up"],
[aria-label="Forgot Password?"],
[aria-label="Resend your 6-digit code."] {
    color: var(--a-color) !important;
}
.profile-avatar:hover::after,
[aria-label="Add the current title to your Watchlist"] {
    border-color: var(--border-color) !important;
}
[aria-label="Add the current title to your Watchlist"]:hover {
    border-color: var(--button-hover-color) !important;
}
[aria-selected="related"]::after,
[aria-selected="extras"]::after,
[aria-selected="details"]::after {
    background-color: var(--border-color) !important;
}
/* <- Update End -> */

/* <- Main Page End -> */
/* <- Player Start -> */
/* <- Show/Movie Title Top Left Start -> */
.btm-media-player .btm-media-overlays-container .overlay__controls .controls__header .control-icon-btn.title-btn {
    color: var(--title-color) !important;
}
/* <- Show/Movie Title Top Left End -> */
/* <- Player Time Start -> */
.btm-media-player .btm-media-overlays-container .overlay__controls .controls .controls__left .time-display-label {
    color: var(--player-time-color) !important;
}
/* <- Player Time End -> */
.btm-media-player .btm-media-overlays-container .overlay__controls .overflow-control > .popup-content .window-flyout-back div,
.btm-media-player .btm-media-overlays-container .overlay__controls .window-flyout-control > .popup-content .window-flyout-back div,
.btm-media-player .btm-media-overlays-container .overlay__controls .overflow-control > .window-flyout-options-control .window-flyout-options-back,
.btm-media-player .btm-media-overlays-container .overlay__controls .window-flyout-control > .window-flyout-options-control .window-flyout-options-back {
    background-image: var(--back-arrow-icon) !important;
}
.btm-media-player .btm-media-overlays-container .overlay__controls .overflow-control > .popup-content .window-flyout-options div,
.btm-media-player .btm-media-overlays-container .overlay__controls .window-flyout-control > .popup-content .window-flyout-options div {
    background-image: var(--options-icon) !important;
}

#\30 19--play > path:nth-child(1) {
    fill: var(--default-btn-c) !important;
}

#\30 20--pause > path:nth-child(1) {
    fill: var(--default-btn-c) !important;
}

#\30 22--rwd-nsec > path:nth-child(1),
#\30 22--rwd-nsec > path:nth-child(2) {
    fill: var(--default-btn-c) !important;
}

#\30 13--ff-nsec > path:nth-child(1),
#\30 13--ff-nsec > path:nth-child(2) {
    fill: var(--default-btn-c) !important;
}

.btm-media-player .btm-media-overlays-container .overlay__controls .controls .progress-bar .slider-fills .now-fill {
    stroke: var(--progressbar-color-a) !important;
}

.btm-media-player .btm-media-overlays-container .overlay__controls .controls .progress-bar .slider-handle {
    background: var(--progressbar-color-b) !important;
}

#\30 43--closed-captioning-on > path:nth-child(1) {
    fill: var(--default-btn-c) !important;
}

#\30 69--arrow-left > path:nth-child(1) {
    fill: var(--default-btn-c) !important;
}

.btm-media-player .btm-media-overlays-container .overlay__controls .controls__footer .audio-control__volume-container .slider-handle {
    background: var(--progressbar-color-b) !important;
}
.btm-media-player .btm-media-overlays-container .overlay__controls .controls__footer .audio-control__volume-container .slider-fills .now-fill {
    stroke: var(--progressbar-color-a) !important;
}

.btm-media-player .btm-media-overlays-container .overlay__controls .controls__footer .audio-control .mute-btn--off div {
    background-image: var(--mute-off-icon) !important;
}

.btm-media-player .btm-media-overlays-container .overlay__controls .controls__footer .audio-control .mute-btn--on div {
    background-image: var(--mute-on-icon) !important;
}
#\30 14--fullscreen > path:nth-child(1) {
    fill: var(--default-btn-c) !important;
}
#\30 11--exit-fullscreen > path:nth-child(1) {
    fill: var(--default-btn-c) !important;
}
.btm-media-player .btm-media-overlays-container .overlay__loading > .loading-icon,
.nFhhK svg,
.nFhhK img {
    background-image: var(--loader-icon) !important;
    background-size: 32px 32px !important;
    background-repeat: no-repeat !important;
    width: 32px !important;
    height: 32px !important;
    box-sizing: border-box !important;
    padding-left: 32px !important;
}
.btm-media-player .btm-media-overlays-container .overlay.overlay__ratings p {
    color: #fff !important;
    border-left: 1px solid var(--border-color) !important;
}
#hudson-wrapper > div > div.btm-media-player.btm-media-player-idle > div.btm-media-overlays-container > div.overlay.overlay__ratings > div.overlay__ratings_wrapper > div.ratings__rating_container > p > span.ratings__rating_rating {
    color: #fff;
}
