.sc-gisBJw:after {
    content: "Disney+ reColor Plus Style by Adam J Frost" !important;
    color: #cacaca !important;
    font-family: Avenir-Roman, sans-serif !important;
    display: block;
    width: 100%;
    margin: 4px 0px 16px 0px;
    padding-top: 5px;
}
/* <- Watermark A End -> */
}

@-moz-document url("https://www.disneyplus.com/account") {
/* <- Watermark B Start -> */
.sc-kcDeIU:after {
