css:
@-moz-document domain("douyu.com") {

    /*====================================================
    适应分辨率: 2560*1440 , 1920*1080
    Chrome: 默认主题; 字号: 中; 缩放: 100%; 显示书签栏
    最终展示为一个完整页面，没有滚动条

    注意：应配合去广告插件使用，例如 Adblock Plus
    ======================================================*/

    span[class^="HeaderGif"],
    .public-DropMenu.Game,
    li[data-arrow="menu_webgame"],
    .Download,
    .downapp,
    .specialHeaderTimerBox,
    .wxr-menu,
    .DropPane-ad,
    .DropMenuList-ad {
        display: none !important;
    }

    .CloudGameLink,
    .Authentication-clientSign,
    .client-sign,
    .Promotion,
    .Task,
    .task {
        display: none !important;
    }

    .Title-columnTag,
    .TitleSuperFansIcon,
    .LuckyStarTitle,
    .Title-sharkWeight,
    .Title-roomOtherBottom>span:nth-of-type(4),
    .Title-row:last-child,
    .Title-anchorPic-bottom {
        display: none !important;
    }

    .Title-row {
        padding-top: 12px !important;
    }

    div[class*="Act"],
    .layout-Player-toolbar *,
    .SuperFansGuideTips,
    .AppFlow,
    .ZoomTip,
    .PcDiversion,
    div[class^="recommendApp"],
    .afterpic-8a2e13,
    .layout-Aside,
    .react-draggable,
    div[class^="normalDiv-"],
    div[class^="closeBg"],
    .PrivilegeGiftModalDialog,
    div[class^="RomanticBroadcast"],
    div[class*=zoomIn],
    .MotorcadeBarrage,
    div[class^="SpringCard"],
    div[class^="recommendAD"],
    .FirstRechargePayPanel,
    div[class^="css-widgetWrapper-Ed"],
    .LadderNav {
        display: none !important;
    }

    .layout-Player-toolbar {
        border: none !important;
        height: 0 !important;
    }

    .layout-Player-video {
        bottom: 0 !important;
    }

    .wm-general-wrapper.bc-wrapper.bc-wrapper-player {
        background-image: none !important;
    }

    .wm-general{
        top: 0 !important;
    }

    .layout-Player-rank,
    .Treasure,
    .TreasureTips,
    .FirePowerChatModal-Notice,
    .FirePowerIcon,
    .BarrageTopic-tip,
    .ShieldTool-PClient,
    .Barrage-notice,
    .FollowGuide,
    .Widget,
    .RoomLevel,
    .Motor,
    div[class^="Barrage-firstCharge"],
    img[title="首充专属徽章"],
    .SignBaseComponent-sign-box,
    .Banner4noble,
    .SysSign-Ad {
        display: none !important;
    }

    .layout-Player-barrage {
        top: 38px !important;
    }

    .layout-Player-guessgame,
    .layout-Bottom {
        display: none !important;
    }

    div[class*="bc-wrapper-"] .wm-h5-view {
        height: 0 !important;
        display: none !important;
    }

    div[class^="bc-wrapper bc-wrapper-"] {
        display: block !important;
        background: none !important;
        max-height: 50% !important;
        padding: 0 !important;
        margin: 0 !important;
        height: 0 !important;
    }

    .wm_footer {
        display: none !important;
    }

    .is-fullScreenPage>#root>div>.layout-Main {
        margin: 0 !important;
        padding: 0 !important;
    }

    a[href*="g.wan.douyu.com"],
    .HeaderCell-corner.is-corner0,
    .HeaderCell-corner.is-corner3 {
        display: none !important;
    }


    /*[[header&title_anchor]]*/

}
