css:
@-moz-document domain("owiki.ms") {
body, html {
    background: #252525!important;
    color: #e8e8e8!important;
}
.mw-body, .noprint, .vectorMenu *, .vectorTabs *, .catlinks {
    background: #252525!important;
    color: #e8e8e8!important;
}
.owiki-home-card {
    background: #383838!important;
    color: #e8e8e8!important;
}
.mw-headline, .firstHeading {
    color: #e8e8e8!important;
}
.toc, .pib-table, .pib-table-usertext, .pib-table-icon, .pib-table-toolbar {
    background: #383838!important;
    color: #e8e8e8!important;
}
h2, h3, .tocnumber {
    color: #e8e8e8!important;
}
a {
    color:#40adff!important;
}
}
