css:
@-moz-document regexp("https://drive.google.com/drive/.*") {
/* Dark Google Drive
   Author:   vaarai
   Based on: https://userstyles.org/styles/176956/conwy-google
   Licence:  https://creativecommons.org/licenses/by-sa/4.0/
*/
html { /* Invert the whole website */
    filter: invert(0.80) hue-rotate(180deg) contrast(1.2) !important;
    background: #ffffff !important;
}
body { /* Hangouts bar */
    background: #ffffff !important;
}
iframe {
    filter: invert(1) hue-rotate(180deg) contrast(1.2)!important;
}
.gbip:before, img, svg:not(.RTiFqe):not(#icon):not(.iron-icon), .RY3tic, .gb_Aa, .gb_Ea, .gb_Ia, .gb_s { /* Inversion of images, svg graphics (avatars, icons) */
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05)!important;
}
img.gb_ua { /* invert G Suite logo */
    filter: invert(0) !important;
}
video { /* Inversion of video and canvas */
    filter: invert(1) hue-rotate(180deg) brightness(1.2) contrast(1.0)!important;
}
.jPtXgd { /* Inversion of guest avatars attending the event */
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05)!important;
}
.gb_q { /* Inversion of avatar in apps menu */
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05)!important;
}
.azp { /* Inversion of avatar in gmail address suggestions */
    filter: invert(1) hue-rotate(180deg) brightness(1.1) contrast(1.05)!important;
}
.llhEMd.llhEMd, .aSs, .NBxL9e, div.Kj-JD-Jh { /* darken background on popup in contacts, gmail compose, calendar */
    background-color: rgba(255, 255, 255, 0.5)
}
.p2ZbV { /* make borders on meet video screen dark */
    background: #fff;
}
.KL4NAf  { /* increase font-size in drive folders */
    font-size: 0.9rem;
}
}
