css:
table[id="group-La Noscea"],
table[id="group-Dravania"],
table[id="group-Gyr Abania"],
table[id="group-Norvrandt"] {
    margin-top: 4.5em !important;
    position: relative;
}

table[id="group-La Noscea"]:before,
table[id="group-Dravania"]:before,
table[id="group-Gyr Abania"]:before,
table[id="group-Norvrandt"]:before {
    display: block;
    width: 100%;
    height: auto;
    position: absolute;
    left: 0;
    top: -1.5em;
    font-size: 200%;
    background: linear-gradient(90deg, transparent 30%, #181818 35%, #181818 65%, transparent 70%),
        linear-gradient(180deg, transparent 48%, white 49%, white 50%, transparent 51%);
}

table[id="group-La Noscea"]:before {
    content: 'A Realm Reborn'
}

table[id="group-Dravania"]:before {
    content: 'Heavensward';
}

table[id="group-Gyr Abania"]:before {
    content: 'Stormblood';
}

table[id="group-Norvrandt"]:before {
    content: 'Shadowbringers';
}
