css:
@-moz-document url-prefix("https://cponline.pw/play/"), url-prefix("https://old.cponline.pw/") {
#notification-area .notification-message {
    display: none;
}
header {
    position: relative;
    height: 32px !important;
    width: 100%;
    background: url(http://web.archive.org/web/20130528171747im_/http://media1.clubpenguin.com/play/images/playNavBg.gif) 0 0 repeat-x;
    z-index: 4;
    margin-bottom: 5px;
    overflow: hidden;
}
body, html {
    background-color: white;
}
nav {
    top: -7px;
}
header nav ul.mainnav a, header nav a.logout {
    transition: none;
    font-family: Arial, Helvetica, sans-serif;
    font-size: 11px;
    font-weight: 700;
    text-decoration: none;
    padding: 0px;
    background-color: transparent;
    border-radius: 0px;
    line-height: 0px;
}
header nav ul.mainnav a:hover, header nav a.logout:hover {
    color: #feb62a;
    background-color: transparent;
    border-bottom: none;
    text-decoration: none;
}
footer {
    font-size: 10.5px;
    color: #a1a1a1;
}
#topAd, #bottomAd {
    display: none;
}
#footer-legal {
    margin-bottom: 14px!important;
}
}
