css:
/* i really want this to be global */
 .fullyLoaded, .CodeMirror-gutters {
    background-color: #f7f7f7;
    color: #4c5157;
}
header {
    background-color: #fff;
    color: #4c5157;
    box-shadow: 0 0 5px #ccc;
    position: relative;
}
#sidebar h3, #sidebar .userSidebar .avatar a, .CodeMirror-wrap pre, #actions .actionItem a.aiButton {
    color: #4c5157;
}
#sidebar, .twitterCont {
    border-color: #ddd;
}
#sidebar .sidebarItem .toggler #resource-counter, #sidebar .sidebarItem .toggler .resourcesLabel, .windowSettings h3, .windowLabel {
    color: #777;
}
#editor .gutter {
    background-color: #ddd;
}
.CodeMirror {
    background: #f7f7f7;
}
.windowLabel {
    background: transparent;
}
#sidebar {
    background: #fff;
    color: #777;
}
.CodeMirror-cursor {
    border-color: #4c5157;
}
.CodeMirror-lint-mark-warning {
    border-bottom: 2px solid #f0d451;
}
.CodeMirror-lint-marker-warning {
    background: #f0d451;
}
pre::selection {
    background: #eee;
    /* WebKit/Blink Browsers */
}
pre::-moz-selection {
    background: #eee;
    /* Gecko Browsers */
}
