@-moz-document domain("ankiweb.net") {
   /* i really want this to be global */
  .container-narrow {
      max-width: 100%;
  }
  .navbar-inner {
      width: 100%;
      position: fixed;
      background: #F8C3CD !important;
      box-shadow: none !important;
      border: 0;
  }
  .pull-right {
      margin-right: 32px !important;
  }
  .row-fluid {
      padding-top: 60px;
  }
  .offset4 {
      margin-left: 24% !important;
  }
  .pull-right a:hover{
      background-color: #4381C1 !important;
      display: inline-block;
      border-radius: 1.2rem;
  }
  .pull-right a:active{
      background-color: #4381C1 !important;
      display: inline-block;
      border-radius: 1.2rem;
  }
  .pull-right a:visited{
      background-color: #4381C1 !important;
      display: inline-block;
      border-radius: 1.2rem;
  } 
}
