css:
@-moz-document domain("jdon.com") {
.highlight {
    font-family:"Operator Mono","Microsoft Yahei",Aria,monospace;
    background: #f1f1f1;
    box-sizing: border-box;
    border-radius:4px;
}
.highlight td {
    padding: 4px !important;
}
.highlight .pl-k {
    color: #d73a49;
}
.highlight .pl-e,
.highlight .pl-en {
    color: #6f42c1;
}
.highlight .pl-s .pl-s1, 
.highlight .pl-smi {
    color: #6f42c1;
}
.highlight .pl-v {
    color: #e36209;
}
}
