css:
@-moz-document domain("300gospodarka.pl") {
html,body{
    background: #1c1c1c;
}
:root{
    scrollbar-color: #049648 #e7282c;
}
/*HEADER*/
.app-wrapper{
    background: #1c1c1c;
}
.app-title a img{
    background: #fff;
}
.app-navigation ul li a{
    color: #049648;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.hot-topics{
    background: #1c1c1c;
}
#column-recommended h3{
    color: #fff;
}
/*NEWSLETTER-WIDGET*/
.textwidget{
    color: #fff;
}
.widget_newsletter{
    background: #049648;
}
.widget_newsletter ::before{
    border-top-color: #e7282c;
}
.widget_newsletter input[type="email"]{
    background: #1c1c1c;
    color: #fff;
    border-color: #e7282c;
}
.widget_newsletter input[type="submit"]{
    background: #1c1c1c;
    border-color: #e7282c;
}
/*NEWSLETTER-WIDGET*/
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
#single article > header{
    background: #1c1c1c;
}
article,article p{
    color: #fff;
}
article .b{
    color: #e7282c;
}
#single #recentPosts h4{
    background: #049648;
}
#single #recentPosts h4 ::before{
    border-top-color: #e7282c;
}
.widget_hot_topics figcaption a{
    color: #049648;
}
#single article #authors figure img{
    background: #fff;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
/*O-NAS*/
#page article > header{
    background: #049648;
}
/*O-NAS*/
/*NEWSY*/
#archive > header{
    background: #049648;
}
#archive > header .icon{
    display: none;
}
#archive .posts-navigation ul li a{
    background: #1c1c1c;
    border: 1px solid;
}
/*NEWSY*/
/*300-SEKUND*/
#archive > header h4{
    color: #1c1c1c;
}
#archive > header h4 a{
    color: #e7282c;
}
/*300-SEKUND*/
/*PODSTRONY*/
/*FOOTER*/
#app > footer .sitemap h3{
    color: #e7282c;
}
.copyright:before{
    content: "Ciemny motyw by Maciejozaman ";
    color: #e7282c;
    font-weight: bold;
}
/*FOOTER*/
}
