css:
@-moz-document url-prefix("https://nos.nl/uitzending/") {
/* Hide navigation urls from page top. */
.nav {
    display: none;
}

/* Set top margin to 0, so it moves the player up. */
#content {
    margin: 0;
}

/* Hide logo located at the right side of the video. */
.broadcast-player-aside {
    display: none;
}

/* Move video flexbox up and add 2% top padding. */
body.page-broadcast #broadcasts-player {
    padding-top: 2%;
}

/* Resize video viewport to fit the parent box. */
.broadcast-player-video {
    width: 100%;
}
}
