css:
@-moz-document domain("lichess.org")
{
  .purple .is2d cg-board,
#top div.color_demo.purple
  {
    background-image: url("https://i.imgur.com/PNFZOcu.png")  !important;
  }

  square.last-move
  {
    will-change: transform;
    background-color: rgba(204,145,34,0.5);
  }

  square.move-dest
  {
    background: radial-gradient(rgba(85,60,20,0.5) 19%, rgba(0,0,0,0) 20%);
    pointer-events: auto;
  }

  square.oc.move-dest
  {
    background: radial-gradient(transparent 0%, transparent 80%, rgba(85,60,20,0.3) 80%);
  }

  square.selected
  {
    background-color: rgba(85,60,20,0.5);
  }
}
