css:
@-moz-document domain(prometheus.io) {

body, p {
	background-color: #222;
	color: #aaa;
}

.navbar-brand img {
	background-color: transparent;
}

.doc-content .open-source-notice, .toc, pre {
	background-color: #333;
	color: #bbb;
}

code {
	background-color: #666;
	color: #ddd;
}

.subtitle {
	background: transparent;
}

.container p {
	background: transparent;
	color: #ddd;
}

.side-nav a {
	color: #ddd;
}

.side-nav div {
	background-color: #333;
}

.nav>li>a:hover {
	background-color: #234;
}

.feature-item h2 {
	color: #ddd;
}

.feature-item:hover {
	background-color: #234;
}

.logos {
	background: #fff;
}

.panel-body {
	background-color: #333;
}

.btn-group button {
	background-color: #ddd;
}

img {
	background-color: #fff;
}
}
