css:
@-moz-document domain("railforums.co.uk") {
html, body
{
    background: 
        #222;
    color: #eee;
}
.messageList .message,
.XenBase .navTabs .navTab.selected .tabLinks,
.navTabs .navTab.selected .tabLinks a,
.breadcrumb .crust a.crumb,
.breadcrumb,
.PageNav a,
.discussionListItem,
.sectionMain,
.primaryContent
{
    background: #333;
    color: #eee;
}
.bbCodeQuote .quoteContainer, .bbCodeBlock {
    background: 
        #222;
    color: #eee;
    border-color: #777;    
}

.messageList .message {
    border: 1px solid #555;    
}
.messageHeading {
    background: #333;
    color: #eee;
    border: 1px solid #555;    
    border-width: 0 0 1px;
}
.bbCodeBlock .type  {
    background: #333;
    color: #eee;
    border-bottom: 1px solid #777;    
}
a:link, a:visited {
    color: #ddf;
}
.pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt,
body .muted, body a.muted, body .muted a,
body .faint, body a.faint, body .faint a {
    color: #ccc;
}
}
