css:
@-moz-document domain("oanda.com") {
/** General page stylings **/
 div, a {
  color: white;
  background-color: black;
}
#page > div {
  color: black;
}
#header, nav, nav:nth-of-type(1) ul, nav:nth-of-type(2) div, .black-font {
  filter: invert(100%);
}
.button.generic-black, .button.generic-black:hover {
  color: white;
}
nav:nth-of-type(2) div {
  background: white;
}
.common-container p, .common-container span, .common-container h2 {
  color: white;
}
#ia.h, #ia.h div, #ia.h ul, #ia.h ul a {
  color: white;
  background-color: black;
}
#CORE_LEFTBLOCK a {
  color: white;
  background-color: black;
}
.lightgray-bg {
  background-color: #484848;
}
/** Controls for different chart types **/
 .heatmap_block {
  color: black;
}
#page table[id$='controls'] td {
  color: black;
  background: white;
}
}
