css:
@-moz-document url("https://habitica.com/") {
.tasks-columns .todo {
    max-width: 100%;
    flex: 0 0 100%;
}
.tasks-columns .habit, .tasks-columns .daily, .tasks-columns .reward {
    display: none;
}

.tasks-columns .todo .tasks-list .sortable-tasks {
    flex-wrap: wrap;
    flex-flow: row wrap;
    display: flex;
}
.tasks-columns .todo .tasks-list .sortable-tasks .task-wrapper {
    width: 25%;
}
}
