css:
@-moz-document domain("tutorial.math.lamar.edu") {
div#page, .top-info-bar {
    background-color: #1f1b24;
}

.notes-nav {
    background-color: #2d2734;
}
.mm-menu {
    color: #eae2c8;
}
.top-nav-bar {
    background-color: inherit;
}
.mm-navbar .mm-breadcrumbs span {
    color: #fff;
}
.header-side-menu-icon {
    background-color: #bc2b2b;
}
.header-side-menu-icon a:link {
    color: #fff;
}
div.content {
    margin-left: auto;
    margin-right: auto;
    width: 45em;
    
    color: #cec299;
    
    line-height: 1.6;
    font-size: 18px;
}

.top-menu-breadcrumb {
    background-color: #2d2734;
    color: #ffffff;
    border: 3px solid #2d2734;
}

h1,h2,h3,h4,h5,h6 {
    box-shadow: -6px 0 #1f1b24, 0 -6px #1f1b24, 6px 0 #1f1b24, 1px 1px 3px;
}

a:link, .SH-Link {
    color: hsl(0, 63%, 66%);
}
a:visited, .SH-Link-Visited {
    color: hsl(281, 47%, 65%);
}
a:hover, .SH-Link-Hover {
    color: hsl(281, 63%, 75%);
}

img {
    filter: invert(90%);
}
}
