css:
@-moz-document domain("amazon.co.uk"), domain("amazon.com"), domain("amazon.ca"), domain("amazon.de"), domain("amazon.it"), domain("amazon.es"), domain("amazon.fr"), domain("amazon.com.mx"), domain("amazon.co.jp"), domain("amazon.com.au") {
.sc-wish-list div.existingwl {
    max-height: 30vh !important;
    width: 50vw !important;
}
.sc-wish-list p {
    width: 100% !important;
    float: left !important;
    white-space: nowrap !important;
    overflow: hidden !important;
    color: transparent !important;
    position: relative !important;
    margin: 0 !important;
    padding: 0.25rem !important;
}
.sc-wish-list p > a {
    position: absolute;
    width: 100%;
    height: 100%;
}
.sc-wish-list p + p {
    margin-top: 0 !important;
}
@media only screen and (min-width: 600px)  {
    .sc-wish-list p {
        width: 49.9% !important;
    }
}
@media only screen and (min-width: 800px)  {
    .sc-wish-list p {
        width: 32.9% !important;
    }
}
@media only screen and (min-width: 1200px)  {
    .sc-wish-list p {
        width: 24.9% !important;
    }
}
@media only screen and (min-width: 1600px)  {
    .sc-wish-list p {
        width: 19.9% !important;
    }
}
}
