css:
@-moz-document domain("zasasa.com") {
/*Main*/

body{
    background: linear-gradient(to right, #000, #555) !important;
    color:#DDD !important;
}
tbody td{
    background:none !important;
}
.menu{
    background:none !important;
}
#url{
    background: linear-gradient(to right, #000, #505) !important;
    border-color:#555 !important;
    color:#DDD !important;
}

/*Exporting Followers*/

#div_data table tr{
    border-color:#CCC !important;
}

/*Buttons & Textareas*/

textarea{
    background-color:#555 !important;
    color:#DDD !important;
}

input[type="button"]{
    background: linear-gradient(to bottom, #000, #505) !important;
    color:#DDD !important;
}
}
