@-moz-document domain("lightnovelstranslations.com") {
body {
    padding-top: 0 !important;
}
body, html, div, p, a, nav, li, span, h1 {
    font-family: "/*[[lntfont]]*/", sans-serif !important;
}
h1 {
    color: #a9a9a9 !important;
    font-weight: bold !important;
}
div#wrapper,
div#main {
    background-color: #1f1f1f;
}
section#container div#content[role=main] {
    margin: 0 auto;
    display: block;
    float: none;
}
section#container div#content[role=main],
section#container div#content[role=main] p {
    font-size: /*[[lntsize]]*/;
    color: #a9a9a9;
    text-align: justify;
}
section#container div#secondary,
div.mtsnb.mtsnb-shown.mtsnb-top.mtsnb-fixed,
ul#prime_nav.menu li.menu-item:last-child {
    display: none;
}
.nav-next a, .nav-previous a {
	background-color: #333333;
}
}
