css:
@-moz-document domain("candlemystar.com") {
.header, footer, .card-box.card.timeline-card, .card-header, .card-content, .card-text, .card-footer, .list.ranking_list, .list.timeline-list, .card-footer::before, .card-footer::after {
    background: #282828;
}
.wrapper {
    background: #121212;
}
.header, footer, .wrapper, .card-text, .list .item-title.comment {
    color: #ffffffde;
}
.logo, .footer-logo img {
    filter: invert();
}
footer, .list.timeline-list {
    border-top-color: #282828
}
.footer-download img {
    border-radius: 8px
}
.card-footer button {
    filter: invert();
}
.card .card-footer .foot-btn, .list .item-title.comment .comment-date, .list .item-subtitle {
    color: #fff9
}
[role="button"], .text-more.translation, .profile-img {
    cursor: auto;
}
.icon-translation, .heart, .star_s, .icon-chat {
    cursor: not-allowed;
}
.carousel-control {
    cursor: pointer;
}
#my-dialog, #dialog-background {
    width: 0;
    height: 0;
    left: -5000px
}
}
