  div.main div.main__detail {
      min-width: 680px;
      width: 100%;
  }
  div.main__articleBody article.ma div.ma__figure {
      width: 600px;
      float: none;
  }

  div.main__articleBody article.ma{
      padding-right: 40px;
  }
  .page__main--listPage {
