css:
/* i really want this to be global */
.bg-blue, .bg-blue:focus, .bg-blue:hover{
  background-color: black !important;
}

.fill-blue-dark {
    fill: #ffffff99 !important;
}

.bg-blue-dark, .bg-blue-dark:focus, .bg-blue-dark:hover {
    background-color: #7a8489 !important;
}

.text-blue-dark {
    color: white !important;
}

.btn.base-btn.btn-blue {
    border: 1px solid #7a8489 !important;
    background-color: #212529 !important;
}

.border-blue, .border-hover-blue:focus, .border-hover-blue:hover {
    border-color: #ff5f5f !important;
}

.text-blue, .text-hover-blue:focus, .text-hover-blue:hover {
    color: #ff5f5f !important;
}

svg.fill-blue, label.fill-blue {
    fill: #ff5f5f !important;
}

.btn.base-btn.btn-flat:not([disabled]):not(.disabled):hover {
    color: #ff5f5f !important;
    fill: #ff5f5f !important;
}

.fill-hover-blue:focus, .fill-hover-blue:hover {
    fill: #ff5f5f !important;
}
