css:
@-moz-document url-prefix("https://my.smartrecruiters.com/companies") {
body, html {
    background: #5a5a5a;
}

.card.Application {
    background: #333333;
    box-shadow: 0 5px 10px -5px #333333;
    border-color: #333333;
}

.color--dark3 {
    color: white !important;
}

.font--medium.margin--top--s {
    color: white;
}

span.status-label {
    color: white;
}

.cp-button--ghost {
    background: none;
    border: 1px solid rgba(255, 255, 255, 0.7);
    color: rgba(255, 255, 255, 0.7);
}

main.app.app--padded {
    background: #5a5a5a;
}
}
