/* i really want this to be global */

@-moz-document domain("facebook.com") {
#facebook ._-kb {
font-family: Roboto Condensed, Helvetica Neue, Helvetica, Tahoma, Arial, sans-serif, SimSun, "Malgun Gothic", "Tai Lanna", "Cambria ASEAN", "Aksara Bali", Yogyakarta, TaiViet, HanaMinA, HanaMinB, Sawndip, SimSun-ExtB, "Noto Serif Ahom", 'Nepal Lipi Unicode' !important
}
}

@-moz-document domain("wiktionary.org"), domain("wikipedia.org") {
.Lana {
    font-family: 'Tai Lanna',sans-serif !important
}

.Bali {
    font-family: 'Aksara bali', 'Noto Sans Balinese',sans-serif !important
}
.Java {
    font-family: 'Yogyakarta', 'Tuladha Jejeg','Javanese Text',adjisaka,'Noto Sans Javanese',sans-serif !important
}
}
