css:
/* Talk To Myself */
@-moz-document domain("app.roll20.net"){
 #textchat-notifier
  {
    position: relative;
      width:92%;
    top: 0px;
      text-align: center;
    background-color: rgb(162, 0, 0);
    color: rgb(255, 255, 255);
    padding: 1px 5px;
    font-size: .7em;
    line-height: 1.1em;
    font-weight: bold;
    text-transform: uppercase;
    border-radius: 10px 10px 10px 10px;
    border: 1px solid hsl(45, 70%, 81%);
    border-top: 0;
    box-shadow: 1px 2px 5px rgba(101, 77, 121, 0.25);
  }
}
