css:
@-moz-document url-prefix("https://wnioski.skript.pl/") {
body {
    background-color: #222;
    background-image: url(https://code.skript.pl/api/background/night);
}

.card
  {
    background-color: #212121!important;
    color: #fff;
  }

.sk-item:hover, sk-item-hover {
    color: inherit;
    text-decoration: none;
    background: #3e3e3e;
}

.sk-item {
    background-color: #212121;
    border-bottom: 1px solid #141414;
}
.sk-neutral {
    color: #bdbdbd;
}
.bg-light{
	background-color: #212121!important;
}
.navbar-light .navbar-nav .nav-link{
    color: #fff !important;
}

.navbar-brand
{
	color: #fff !important;
}
.dropdown-item {
    color: #fff;
    background-color: #212121;                                          
}
.dropdown-menu{
	background-color: #212121; 
}
.dropdown-item:focus, .dropdown-item:hover {
    color: #fff;
    text-decoration: none;
    background-color: #3e3e3e;
}
.form-control{
	color: #fff;
    background-color: #333;
	border: 1px solid #141414;
}
.form-control:focus {
    color: #fff;
    background-color: #444;
}
.input-group-text {
    color: #fff;
    background-color: #333;
    border: 1px solid #141414;
}
.sk-label[style='background: #222222; ']{
    background: #5f5f5f!important;
}

.sk-label[style='background: #222222; opacity: 0.5;']{
	background: #5f5f5f!important;
}
}
