css:
/* Roll20 Zoom Slider Tweaks */
@-moz-document domain("app.roll20.net"){
.zoomplus, .zoomminus {
    font-size: 7px;
}

#zoomclick{
  opacity: 0.8;
}

#zoomclick .btn {
    width: 2.8px;
    height: 5px;
    padding-left: 8px;
}

#zoomslider a.ui-slider-handle.ui-state-default.ui-corner-all {
    left: -.5em;
    height: 10px;
    width: 16px;
}

.zoomValue {
    font-size: 12px;
    font-family:"arial black";
    background-color: #22222247;
    padding: 0px;
}
}
