css:
@-moz-document domain("github.com"), domain("gist.github.com"), domain("raw.githubusercontent.com") {
.tab-size[data-tab-size='2'],
.tab-size[data-tab-size='4'],
.tab-size[data-tab-size='8'],
.inline-review-comment,
.gist table.lines,
table.diff-table,
.markdown-body pre,
body > pre {
	tab-size: /*[[ts]]*/ !important;
}
}
