css:
@-moz-document domain("biznesalert.pl") {
html,body{
    background: #1c1c1c;
    color: #fff;
}
:root{
    scrollbar-color: #d51019 #1c1c1c
}
/*HEADER*/
input[type="text"]{
    background: #1c1c1c;
    border: 1px solid #d51019;
    color: #fff;
}
/*HEADER*/
/*STRONA-GŁÓWNA*/
.main-container,.homepage-block{
    background: #1c1c1c;
}
.news-list-name{
    color: #d51019;
}
.news-list-window .news-list-name:hover, .news-list-window .news-list-name:focus{
    color: #fff;
}
/*STRONA-GŁÓWNA*/
/*ARTYKUŁ*/
.article-header h1,.author-box-name{
    color: #d51019;
}
.author-box-name:hover{
    color: #fff;
}
.article-tags a{
    background: transparent;
    border: 1px solid #d51019;
    color: #d51019;
}
.article-tags a:hover{
    background: #d51019;
    color: #1c1c1c;
}
/*ARTYKUŁ*/
/*PODSTRONY*/
/*PROFIL-AUTORA*/
.category-header span{
    color: #d51019 !important;
}
/*PROFIL-AUTORA*/
/*PODSTRONY*/
/*FOOTER*/
.footer{
    text-align: center;
}
.footer-bottom:after{
    content: "Ciemny motyw by Maciejozman";
}
/*FOOTER*/
}

@-moz-document url-prefix("https://forms.freshmail.io/f/blz4y7ipbl/lodixbjjsq/index.html") {
.fmForm{
    background: #1c1c1c;
    border: 1px solid #d51019;
    padding-bottom: 18px;
}
.fmForm input[type="text"], .fmForm select, .fmForm input[type="email"]{
    background: #1c1c1c;
    color: #fff;
    border: 1px solid #d51019;
}
.fmForm .form-checkbox .checkbox-text *{
    color: #fff;
}
}
