*/
@-moz-document domain("junkburst.wordpress.com") {
html, body,
div#page {
    background-color: #1a1a1a;
    color: #aaaaaa !important;
}
body, p, div, span, h1, h2, h3, h4, a {
    font-family: "/*[[jbtfont]]*/", sans-serif !important;
}
h1, h2, h3, h4, a, cite, em, strong {
    color: #eeeeee !important;
}
header div.site-header-image {
    min-height: initial;
    height: auto;
    padding: 2em 0;
    filter: invert(1);
}
header div.site-header-image div.site-branding-wrapper {
    filter: invert(1);
}
div.entry-content {
    font-size: /*[[jbtsize]]*/ !important;
}
div#secondary.widget-area {
    display: none;
}
main#main.site-main {
    margin: 0;
}
div#actionbar,
div#actionbar span,
div#actionbar div.actnbr-popover.tip div.tip-inner,
div#actionbar div.actnbr-popover.tip div.tip-inner li,
div#actionbar div.actnbr-popover.tip div.tip-inner li a {
    background-color: #333;
    color: #888 !important;
    border-color: #555;
}
div#actionbar div.actnbr-popover.tip div.tip-arrow:before {
    border-color: #555;
    border-left-color: transparent;
    border-right-color: transparent;
}
}
