css:
@-moz-document regexp('https?://[\w]+\.baidu\.com/?')
{
body
{
	width:100%;
	height:99%;;
  	overflow:hidden;
}


/* 登陆状态 */

/*  去掉右上角的更多产品 */
#u_sp > a.s_bri
{
	display:none
}

/* 去掉更多产品后调整选项卡位置 */
span[class='setting-text']
{
	margin-right:15px
}


/* 去掉最上方横条 */
#u_sp,#s_upfunc_menus,#s_top_wrap
{
/* 	display:none */
}


/* ---------------------------------------------------------------------------- */

/* 未登录状态 */

/* 未登录状态 去掉右上角更多产品 */
#u1 > a.bri
{
	position:absolute;
	z-index:0;
	right:-100px;
}


/* 调整最上方选项卡位置 */
#u1
{
	position:absolute;
	right:-60px
}


/* 去掉最上方横条 */
#u1
{
/* 	display:none */
}




/* ---------------------------------------------------------------------------- */

/* 隐藏二维码等内容  */
div[class="qrcodeCon "],div[class="ftCon-Wrapper"]
{
	display:none
}


/* 去掉最下面一排 */
#bottom_layer
{
	display: none
}
}
