* Source: https://userstyles.org/users/867056 
*/
@-moz-document domain("shirokuns.com") {
html, body {
    background-color: #1a1a1a;
    color: #aaaaaa !important;
}
body, p, div, span, h1, h2, h3, h4, a {
    font-family: "/*[[sktfont]]*/", sans-serif !important;
    text-align: justify;
}
h1, h2, h3, h4, a, cite, em, strong {
    color: #eeeeee !important;
}
header.header,
nav.navbar,
div.card {
    background-color: #222222;
    color: #aaaaaa !important;
}
i.fas.fa-search {
    background-color: transparent !important;
    color: #eeeeee !important;
}
div.wrapper.post,
div.main {
    background-color: #1a1a1a;
    color: #aaaaaa !important;
}
div.container article div.single-post-wrap p,
div.container article div.single-post-wrap p span,
div.container article div.single-post-wrap table,
div.container article div.single-post-wrap table span {
    font-size: /*[[sktsize]]*/ !important;
}
div.container article div.single-post-wrap table {
    width: 700px !important;
    max-width: 100% !important;
    margin: 0 auto;
    background-color: #222222 !important;
}
div.container article div.single-post-wrap table td {
    border: 1px solid #eee;
}
div.container article div.single-post-container {
    width: 100%;
}
div.container article div.blog-sidebar-wrapper {
    display: none;
}
span.label.label-primary,
span.label.label-primary a {
    background-color: #aaaaaa !important;
    color: #1a1a1a !important;
}
div#comments div.media {
    border-bottom: 1px solid #aaaaaa;
    padding: 10px 0;
}
div#comments div.media div.media-body p {
    color: #aaaaaa;
}
button.hestia-scroll-to-top.hestia-fade,
a.btn.btn-just-icon.btn-round,
i.fas {
    background-color: #aaaaaa !important;
    color: #1a1a1a;
}
}
