css:
@-moz-document domain("flightrising.com") {
.post-text-signature {
    display: none !important;
}
.post-text {
    height: auto !important;
}
.post-frame {
    min-height: auto !important;
    padding-bottom: 12px;
}
div.post-text-content {
    margin-bottom: 0px;
    min-height: 190px !important;
}
.post-text-footer {
    position: static;
}
}

@-moz-document url-prefix("https://www1.flightrising.com/forums") {
div.common-tooltip-box {
    display: none !important;
}
}
