    color: #7b7b7b;
}
.modul.quickfinder .tab-content {
    background: #2f2f2f;
}
.modul.quickfinder fieldset {
    background: #2f2f2f;
}
.clearfix {
	background:#353535;
}
#inhalt {
	background:#353535;
}
.section.teasers {
	background:#353535;
}
.section.teasers.third {
	background:#353535;
}
#language-selector ul {
    background-color: #000;
}
}
