css:
@-moz-document domain("mangakakalots.com"), domain("mangakakalot.com") {
  /* main background */
  html,
  body {
      background: #21252d;
  }

  .logo_chapter {
      background: #21252d;
  }

  .top-header {
      background: #21252d;
  }

  .searching form input {
      background: #414753;
       color: #b9b9c4; 
  }

  /* home page */

  .doreamon {
      background: #21252d;
  }

  .doreamon div ul li span a {
     color: #b9b9c4; 
  }

  .panel-category table tbody tr {
      background: #21252d;
  }

  .panel-category table tbody tr:nth-child(even) {
      background: #424855;
  }

  .panel-category table tbody tr td a {
      color: #b9b9c4; 
  }


  /* latest updates / hot manga / new manga / completed manga */

  /* cards where the manga info is in */
  .list-truyen-item-wrap {
      background: #414959;
  }

  /* description of manga */
  .list-truyen-item-wrap p {
      color: #d6d6dd;
  }

  /* title of manga */
  .list-truyen-item-wrap h3 a {
      color: #b9b9c4;
  }



  /* when clicked on a manga */

  /* main card background */
  .manga-info-top {
      background: #414959;
  }

  /* main card text (important needed to overwrite color) */
  .manga-info-text li {
      color: #b9b9c4 !important;
  }

  #rate_row_cmd {
      color: #b9b9c4 !important;
  }

  /* description background and color (important needed to overwrite color) */
  #noidungm {
      background: #414959 !important;
      color: #b9b9c4 !important;
  }

  /* show more / show less background */
  .content-extent {
      background: #414959;
  }

  /* chapter list background */
  .manga-info-chapter {
      background: #414959;
  }

  /* chapter list (chapter name) */
  .chapter-list span a {
      color: #b9b9c4;
  }

  /* chapter list (view nad time uploaded) */
  .chapter-list div span {
      color: #b9b9c4;
  }

  /* comment section - cant change text color since its an iframe */
  .comment-info {
      background: #636c7d;
  }
}
