css:
@-moz-document url-prefix("https://roamresearch.com/#/app/"), url-prefix("https://roamresearch.com/#/v8/") {

.roam-body-main {
    background-color: #2f3437;
}
.roam-topbar {
    background-color: #182026;
}
.bp3-input {
    background: #FFFFFF;
    background: rgba(255, 255, 255, .05);
}
.roam-body .roam-app {
    color: #FFFFFF;
    color: rgba(255, 255, 255, .60);
}
.rm-reference-item {
	background-color: #121212;
    background-color: rgba(255, 255, 255, .08);
}
.rm-title-display {
    color: #FFFFFF;
    color: rgba(255, 255, 255, .87);
}
h1, h2, h3 {
    color: #FFFFFF !important;
    color: rgba(255, 255, 255, .87) !important;
}
.check-container input:checked ~ .checkmark {
    background-color: #FFFFFF;
    background-color: rgba(230, 126, 34, 1.0);
}
.rm-page-ref-link-color, a {
    color: #ff9800;
    color: rgba(255, 152, 0, 1.0);
}
a:focus, a:hover {
    color: #ffc107;
    color: rgba(255, 152, 0, .87);
}
.rm-page-ref-tag {
    color: #FFFFFF;
    color: rgba(255, 255, 255, .38);
}

.block-highlight-yellow {
    background-color: #795548;
    background-color: rgba(121, 85, 72, .60);
}

code {
	background: #121212;
    background: rgba(18, 18, 18, 0.60);
    color: #ff5722;
    border: 1px solid #000000;
    border: 1px solid rgba(18, 18, 18, 0.87);
}
.bp3-elevation-3 {
	background-color: #182026 !important;
}
#right-sidebar {
	background-color: #182026 !important;
}

.roam-block > span > div {
	background-color: #182026 !important;
}

.roam-block .rm-block-ref {
	background-color: transparent !important;
}

.rm-block-ref:hover {
	background-color: transparent !important;
    border-bottom: 1px solid rgba(255, 152, 0, 1.0);
}
}
