css:
@-moz-document regexp('.*geoguessr.com/map-maker.*')
{
.map-maker-map {
    width: 100%;
    height: 40rem;
}
.title:after {
    margin: 0.5rem auto 1.0rem;
    margin-bottom: 12px;
}
.streetview-panel {
    width: 560px;
    height: 400px;
}
textarea.text-input {
    min-height: 8rem;
}
.margin--top-large {
    margin-top: 20px;
}
.layout__main {
    padding-top: 10px;
}
}
