css:
@-moz-document domain("wiki.mediviastats.info") {
#mw-content-container {
    background-image: none !important;
    background: none !important;
}

#mw-content-wrapper {
    margin-top: 15px !important;
    background-image: none !important;
    background: none !important;
    font-size: 90% !important;
}

#mw-footer-container {
    position: relative !important;
    margin-bottom: -22px !important;
}

#lastmod {
    padding-left: 20px !important;
}

#mw-site-navigation {
    margin-left: 10px !important;
    font-size: 85% !important;
}

#mw-related-navigation {
    margin-right: 10px !important;
    font-size: 85% !important;
}

#page-tools {
    margin-top: 15px !important;
}

#toc {
    font-size: 90% !important;
    opacity: 0.9 !important;
}

.oo-ui-tagMultiselectWidget-handle {
    background-color: #222 !important;
    opacity: 0.85 !important;
}

.oo-ui-inputWidget-input {
    border-left-color: rgb(162, 169, 177) !important;
    border-top-color: rgb(162, 169, 177) !important;
    border-bottom-color: rgb(162, 169, 177) !important;
    opacity: 0.85 !important;
    background-color: #222 !important;
}

.mw-rcfilters-ui-menuSelectWidget {
    font-size: 80% !important;
}

.oo-ui-popupWidget {
    font-size: 80% !important;
    color: black !important;
}

.mw-changeslist-legend {
    background-color: #222 !important;
    opacity: 0.85 !important;
    border-color: rgb(162, 169, 177) !important;
}

#wpPreview,
#wpDiff {
    color: white !important;
}

#mw-editform-cancel {
    opacity: 0.9 !important;
    background-color: #222 !important;
}

.transcripts {
    font-size: small !important;
}

body {
    background-image: url(https://wiki.mediviastats.info/skins/Timeless/resources/images/bg.jpg) !important;
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center top;
    background-size: cover;
}
}
