css:
::-webkit-scrollbar {
    width: 3px;
    height: 3px;
}

::-webkit-scrollbar-track-piece {
    background-color: white;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}

::-webkit-scrollbar-thumb:vertical {
    background-color: #666;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}
::-webkit-scrollbar-thumb:horizontal {
    background-color: #666;
    -webkit-border-radius: 3px;
    border-radius: 3px;
}
