css:
@-moz-document url-prefix("https://twitter.com/"), url-prefix("https://mobile.twitter.com/") {
/** --------------------------- */
/** Remove distracting sidebar */

[aria-label='Timeline: Trending now'] {
    display: none;
}

[aria-label='Who to follow'] {
    display: none;
}

[class='css-1dbjc4n r-1uaug3w r-1uhd6vh r-t23y2h r-1phboty r-rs99b7 r-ku1wi2 r-1bro5k0 r-1udh08x'] {
    display: none;
}

/** --------------------------- */
/** Remove unnecessary tabs */

[aria-label='Home'] {
    display: none;
}

[aria-label='Home (New unread Tweets)'] {
    display: none;
}

[href='/explore'] {
    display: none;
}

[href='/i/bookmarks'] {
    display: none;
}

/** --------------------------- */
/** Removes notification badges in Notification and profile chooser (for multiple accounts) **/

[class='css-901oao r-1awozwy r-urgr8i r-f6ebdl r-sdzlij r-1phboty r-rs99b7 r-1tjplnt r-jwli3a r-6koalj r-1q142lx r-1qd0xha r-1gkfh8e r-16dba41 r-50lct3 r-1777fci r-ad9z0x r-1b8eohn r-u8s1d r-kquydp r-1m4drjs r-3s2u2q r-qvutc0'] {
	display: none;
}

[class='css-901oao r-1awozwy r-urgr8i r-f6ebdl r-sdzlij r-1phboty r-rs99b7 r-1tjplnt r-jwli3a r-6koalj r-1q142lx r-1qd0xha r-n6v787 r-vw2c0b r-50lct3 r-1777fci r-1sf4r6n r-1n0xq6e r-1b8eohn r-3s2u2q r-qvutc0'] {
    display: none;
}

[class='css-1dbjc4n r-urgr8i r-f6ebdl r-sdzlij r-1phboty r-rs99b7 r-k5y9r7 r-u8s1d r-1k4ycdi r-3dc97n r-1t7zxhp'] {
	display: none;
}

/** --------------------------- */
/** Removes followings/followers bar in user profile **/

[class='css-1dbjc4n r-18u37iz r-1w6e6rj'] {
    display: none;
}

/** --------------------------- */
/** Removes 'followed by someone' text in user profile **/

[class='css-1dbjc4n r-bt1l66'] {
    display: none;
}

/** ------------------------ **/
/** Hides profile picture sitewide, except in profile */
/** pfp: normal tweets */
[class='css-1dbjc4n r-1awozwy r-18kxxzh r-zso239'] {
    display: none;
}

/** pfp: Who to follow panel */
[class='css-1dbjc4n r-18kxxzh r-1h0z5md r-zso239'] {
    display: none;
}

/** pfp: Show this thread */
[class='css-1dbjc4n r-1awozwy r-6koalj r-18kxxzh r-zso239 r-bnwqim'] {
    display: none;
}

/** pfp: Quoted tweet */
[class='css-1dbjc4n r-sdzlij r-1q142lx r-z80fyv r-7o8qx1 r-1ny4l3l r-1udh08x r-19wmn03'] {
    display: none;
}

/** pfp: In Search list result (hovered) */

[class='css-1dbjc4n r-18kxxzh r-1777fci r-zso239'] {
    display: none;
}

/** ------------------------ **/
/** Hides Direct Message popup */

[class='css-1dbjc4n r-rull8r r-qklmqi r-f6ebdl r-1n0n9yc r-1g4ueg6 r-1loqt21 r-cgjvx2 r-184en5c'] {
	display: none;
}

/** --------------------------- */
/** Small margin spacing for the OCD */

[class='css-1dbjc4n r-obd0qt r-18kxxzh r-5f2r5o'] {
    flex-basis: 10px !important;
}

[class='css-1dbjc4n r-1awozwy r-6koalj r-18kxxzh r-5f2r5o r-bnwqim'] {
    flex-basis: 0 !important;
}

/** --------------------------- */
}
