css:
@-moz-document domain(developer.wordpress.org) {
 .single-handbook, #primary, #page, #secondary-content, blockquote, #secondary, #wporg-footer, #search-field{
    background: #101010 !important;
 }
 a{
    color: #3ea6ff !important;
 }
 .table-of-contents {
    background: #1f1f1f !important;
  	border: none !important;
 }
  p, li, h1, h2, h3, h4, h5, h6, td span, dd, span, *::before{
  	color: #cacaca !important;
  }
  dt{
  color: #ff97f6 !important;
  }
  code{
  	color: #ff3939 !important;
  }
  button.dashicons.dashicons-arrow-down-alt2, .active {
    background: #1b1b1b !important;
  }
  .active {
  	border-right: 30px solid #4e4e4e !important;
  }
}
