css:
@-moz-document domain(docs.openio.io)
 {
body
{
    background-color:black;
}

.section
{
    color:white;
}

div.highlight, code
{
    background-color:black;
    color:white;
}

cite
{
    background-color:black;
}


select.switch-version
{
    color:white;
}

#searchbox .search
{
    background-color:black;
    color:white;
}

.sphinxsidebarwrapper
{
    background-color:black;
}

.sphinxsidebarwrapper .toctree-l1 > a
{
    color:white;
}

.sphinxsidebarwrapper li.toctree-l2 > a
{
    color:white;
}

.sphinxsidebarwrapper .toctree-l1.toctree-open > a
{
    color:white;
}

.sphinxsidebarwrapper .toctree-l1:hover > a
{
    color:wheat;
}

table
{
    background-color:black;
}

th, .admonition
{
    background-color:grey;
    color:white;
}

.code-block-caption
{
    background-color:black;
}

h1, h2, h3
{
    color:white;
}
}
