css:
@-moz-document url-prefix("https://www.digi24.ro/live/digi24") {
.video-player {
	max-width: 100%!important;
}
.header {
	-webkit-box-shadow: 0 8px 0 #2F2F2F!important;
	box-shadow: 0 8px 0 #2F2F2F!important;
}
.logo {
	background: #A5A5A5 url(https://upload.wikimedia.org/wikipedia/commons/thumb/d/df/Logo_Digi_24_%282012%29.svg/1200px-Logo_Digi_24_%282012%29.svg.png) center no-repeat!important;
	background-size: 100% 100%!important;
}
.live-page.container .col-8 {
	-ms-flex-preferred-size: 100%!important;
	flex-basis: 100%!important;
	max-width: 100%!important;
}
main,
.widget-shows.bg.bg-digi24 {
	background-color: #222!important;
}

.bg:before {
	opacity: 0.02!important;
}
.section-title:after,
.section-title:before {
	background: #333!important;
	height: 1px!important;
}
.article-title a,
.article-date,
.section-subtitle,
p {
	color: #A5A5A5!important
}
.section-subtitle {
	border-bottom: 1px solid #333!important;
}
.swiper-widget-article .swiper-button-next,
.swiper-widget-blog .swiper-button-next,
.swiper-widget-shows .swiper-button-next,
.swiper-widget-videos .swiper-button-next,
.swiper-button-next,
.swiper-button-prev {
	background: #2F2F2F!important;
}
.section-title,
a.read-more,
p a {
	color: #733AF7!important;
}
}
