css:
@-moz-document domain(connexus.com) {
* {
    transition-duration:5s;
	transition-timing-function: ease-out;
	animation-name:startgrow;
	animation-duration:60s;
	animation-timing-function:cubic-bezier(0.480, 0.000, 0.000, 1.000); /* custom */
	animation-iteration-count:1;
}
.ee-parent-panel * {
	animation-duration:0s;
}
.progress-wrapper * {
    transition-duration:45s;
    transition-timing-function: ease-in
}
@keyframes startgrow {
	0% {width:0px;height:0px;margin:0px;padding:0px;letter-spacing:-300px;word-spacing:-3px;font-size:0px;font-stretch:ultra-expanded;transform:rotate(5000deg);overflow:visible;}
	10% {letter-spacing:-2px;}
	80% {transform:rotate(-200deg);overflow:visible;}
}
.lessonTree {
	animation-duration:5s;
}
.lvIcon {
	transition: all 0.5s steps(3);
}
.lvIcon:hover {
	border-radius:100%;
}
.ui-droppable .ui-draggable {
	animation-duration:2s;
}
}
