css:
@-moz-document url-prefix("https://www.roblox.com/games/"), url-prefix("https://web.roblox.com/games/") {
.light-theme a.btn-common-play-game-lg, .dark-theme a.btn-common-play-game-lg {
    background: url(http://web.archive.org/web/20110713000716im_/http://www.roblox.com/images/cssspecific/rbx2/btn_play_54h.png) no-repeat;
    border: 0px;
    width: 212px;
    height: 54px;
}
.light-theme a.btn-common-play-game-lg:hover, .dark-theme a.btn-common-play-game-lg:hover {
    background-color: transparent;
    background-position: 0 -54px!important;
}
.game-main-content .game-play-buttons .btn-common-play-game-lg {
    min-width: 0px;
    padding: 0px;
}
.light-theme .icon-play-game,.dark-theme .icon-play-game {
    display: none;
}
}
