@-moz-document domain("trello.com") {
.badges {
    width: 100%;
}
.js-badges {
    margin-top: 7px;
    border-top: 1px solid #ccc;
    padding-top: 7px;
    padding-bottom: 5px;
    border-bottom: 1px solid #ccc;
    margin-bottom: 7px;
    display: block;
}
.custom-field-front-badges.js-custom-field-badges span div.badge {
    display: block;
}
}
