css:
@-moz-document domain("atlassian.net") {
.ghx-issue {
    font-size: 36px;
}

#ghx-header {
    visibility: collapse !important;
}

#ghx-operations  {
    visibility: collapse !important;
    position: absolute;
}

#navigation-app {
    visibility: collapse !important;
    position: absolute;
}

#ghx-rabid {
    position: fixed !important;
}

.ghx-row.ghx-stat-1 {
    visibility: collapse !important;
    position: absolute;
}

.ghx-issue .ghx-avatar-img {
    width:50px;
    height:50px;
    padding:0px;
}

.ghx-issue .ghx-stat-fields > .ghx-row {
    margin-top: 0px;
}


.ghx-parent-stub {
    font-size: 30px;
}

.ghx-highlighted-field {
    visibility: collapse !important;
    position: absolute;
}
}
